mapper.update:
update `sys_user`
nickname = #{nickname,jdbcType=VARCHAR}, phone = #{phone,jdbcType=VARCHAR}, email = #{email,jdbcType=VARCHAR}, password = #{password,jdbcType=VARCHAR}, salt = #{salt,jdbcType=VARCHAR},
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, nickname, phone, email, password, salt, gmt_create, gmt_update
from `sys_user`
where id = #{id,jdbcType=VARCHAR}
